local function random(min, max)
    return min + math.random() * (max - min)
end

local function findItemExam(text)
    local result = {}
    local offset = 0

    while true do
        local startTag = text:find("<itemData>")
        local _, endTag = text:find("</itemData>")

        if not startTag or not endTag then
            break
        end

        table.insert(result, {
            text = text:sub(startTag, endTag),
            startPos = startTag + offset,
            endPos = endTag + offset,
        })

        offset = offset + endTag
        text = text:sub(endTag + 1)
    end

    return result
end

local function parseItemExam(xml)
    local function escape(str)
        if not str then
            return nil
        end

        str = str:match("^%s*(.-)%s*$")
        if str == "" then
            return nil
        end

        local replacements = {
            ["&"] = "&amp;",
            ["<"] = "&lt;",
            [">"] = "&gt;",
            ['"'] = "&quot;",
            ["'"] = "&#39;",
        }

        return (string.gsub(str, "[&<>'\"]", replacements))
    end

    local function extractTag(tag)
        local pattern = "<" .. tag .. ">(.-)</" .. tag .. ">"
        local result = string.match(xml, pattern)

        return escape(result)
    end

    local itemData = {}

    itemData.name = extractTag("name")
    itemData.emoji = extractTag("emoji")
    itemData.type = extractTag("type")
    itemData.rarity = extractTag("rarity")
    itemData.attunement = extractTag("attunement")
    itemData.bonuses = extractTag("bonuses")
    itemData.charges = extractTag("charges")
    itemData.appearance = extractTag("appearance")
    itemData.lore = extractTag("lore")

    -- Check if item is unidentified (has ??? in name or rarity)
    if itemData.name == "???" or itemData.rarity == "???" then
        itemData.isUnidentified = true
    end

    return itemData
end

