    content: """;
    font-size: 36px;
    color: var(--text);
    opacity: 0.3;
    position: absolute;
    top: -10px;
    left: 4px;
}

.itemexam-lore::after {
    content: """;
    font-size: 36px;
    color: var(--text);
    opacity: 0.3;
    position: absolute;
    bottom: -30px;
    right: 4px;
}

.itemexam-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 4px;
    margin-top: 16px;
}

.itemexam-type {
    color: var(--text);
    padding: 3px 8px;
    border: 1px solid var(--text);
    border-radius: 4px;
    background: rgba(9, 18, 30, 0.4);
    text-shadow: 0 0 3px var(--text-shadow);
    box-shadow: 0 0 5px var(--text-shadow);
    transition: all 0.3s ease;
}

.itemexam-type:hover {
    box-shadow: 0 0 15px var(--text);
}

.itemexam-glitch {
    animation: itemexam-glitch 4s infinite;
}

.itemexam-star {
    position: absolute;
    background-color: var(--text);
    border-radius: 50%;
    opacity: 0;
    z-index: 3;
}

.itemexam-rays {
    position: absolute;
    top: -200%;
    left: -200%;
    bottom: -100%;
    right: -100%;
    opacity: 0.07;
    filter: blur(8px);
    animation: itemexam-ray 120s linear infinite;
    z-index: 2;
}

.itemexam-ray {
    background: linear-gradient(to top, #ffffff, transparent 30%);
    border-radius: 80% 80% 0 0;
    position: absolute;
    height: 100%;
    top: 50%;
    left: 50%;
    transform-origin: center bottom;
}

@keyframes itemexam-artifact-bg {
    0% {
        background-position: left top;
    }

    100% {
        background-position: right bottom;
    }
}

@keyframes itemexam-pulse {
    0%, 100% {
        opacity: 0.8;
    }
    50% {
        opacity: 1;
    }
}

@keyframes itemexam-mystery-pulse {
    0%, 100% {
        filter: blur(3px);
        opacity: 0.7;
    }
    50% {
        filter: blur(4px);
        opacity: 0.9;
    }
}

@keyframes itemexam-glitch {
    0%, 90%, 100% {
        opacity: 1;
        transform: translate(0);
    }

    90.5% {
        opacity: 0.8;
        transform: translate(-3px, 1px);
    }

    91% {
        opacity: 1;
        transform: translate(0);
    }

    91.5% {
        opacity: 0.9;
        transform: translate(2px, -2px);
    }

    92% {
        opacity: 1;
        transform: translate(0);
    }

    92.5% {
        opacity: 0.8;
        transform: translate(2px, -5px);
    }

    93% {
        opacity: 1;
        transform: translate(0);
    }
}

@keyframes itemexam-fall {
    0% {
        top: -5px;
        transform: translateY(0);
        opacity: 0;
    }

    10% {
        opacity: 0.8;
    }

    90% {
        opacity: 0.8;
    }

    100% {
        transform: translateY(1000px);
        opacity: 0;
    }
}

@keyframes itemexam-ray {
    0% {
        transform: rotate(0deg);
    }

    100% {
        transform: rotate(360deg);
    }
}

@media (max-width: 600px) {
    .itemexam-wrapper {
        width: 100%;
    }

    .itemexam-image {
        min-width: 50px;
        min-height: 50px;
        line-height: 50px;
    }
}

