        table.insert(result, string.format('<span class="itemexam-property-value">%s</span>', itemData.charges))
        table.insert(result, '</div>')
    end

    -- Appearance
    if itemData.appearance then
        table.insert(result, '<div class="itemexam-property">')
        table.insert(result, '<span class="itemexam-property-label">Appearance:</span>')
        table.insert(result, string.format('<span class="itemexam-property-value">%s</span>', itemData.appearance))
        table.insert(result, '</div>')
    end

    table.insert(result, '</div>') -- itemexam-properties

    -- Lore section
    if itemData.lore then
