    end

    table.insert(result, '<div class="itemexam-divider"></div>')

    table.insert(result, '<div class="itemexam-footer">')

    -- Format the type display based on item identification status
    local typeDisplay = itemData.type or "???"
    local rarityDisplay = rarityMap[string.lower(itemData.rarity or "")] or "???"

    if itemData.isUnidentified or rarityClass == "unidentified" then
        typeDisplay = "Unidentified"
        if itemData.type and itemData.type ~= "???" then
            typeDisplay = itemData.type
        end
    end

    table.insert(result, string.format('<span class="itemexam-type">%s ‚Ä¢ %s</span>', typeDisplay, rarityDisplay))
    table.insert(result, '</div>') -- itemexam-footer

    -- Add visual effects based on rarity
    if addStar then
        table.insert(result, '{{#if {{greater_equal::{{chat_index}}::{{? {{lastmessageid}} - 5}}}}}}')
        table.insert(result, '<div class="itemexam-stars">')
        for _ = 1, 100 do
