        end
        table.insert(result, '</div>') -- itemexam-rays
        table.insert(result, '{{/if}}')
    end

    table.insert(result, '</div>') -- itemexam-wrapper
    table.insert(result, '<div class="itemexam-dummy"></div>')

    return table.concat(result, "\n")
end

listenEdit("editDisplay", function(triggerId, data)
    if not data or data == "" then
        return ""
    end

    local lastPos = 1
    local output = ""
    local excludeStyle = false

    local success, itemExams = pcall(findItemExam, data)
    if not success then
        print("[itemExam] Error finding itemData elements :: findItemExam ::")
    end

    for _, itemExam in ipairs(itemExams) do
        if lastPos < itemExam.startPos then
            output = output..data:sub(lastPos, itemExam.startPos - 1)
        end

        local parseSuccess, itemData = pcall(parseItemExam, itemExam.text)
        if not parseSuccess then
            print("[itemExam] Error parsing itemData element :: parseItemExam ::")
            output = output.."!! Item Data Error !!"
        else
            local beautifySuccess, result = pcall(beautify, itemData, excludeStyle)
            if not beautifySuccess then
                print("[itemExam] Error beautifying itemData element :: beautify ::")
                output = output.."!! Item Data Error !!"
            else
                output = output..result
            end
        end

        lastPos = itemExam.endPos + 1
        excludeStyle = true
    end

    if lastPos <= #data then
        output = output..data:sub(lastPos)
    end

    return output
end)

